import React from 'react';
import { Select as DsSelect } from '@dotkomonline/design-system';
import styled from 'styled-components';

interface SelectProps {
  name: string;
  options: [{
    value: string;
    label: string;
}];
}

const SelectBase = ({...props }: SelectProps) => {
    const [selectedOption, setSelectedOption] = useState<ValueType<OptionType>>(options[0]);

  const handleChange = (option: ValueType<OptionType>) => {
    setSelectedOption(option);
  };

  return
};

const Select = styled(SelectBase)`
  & + div {
    & > i {
      font-size: 1.2rem;
      position: absolute; /* Required or else the checkmark looks fucked*/
    }
  }
`;

export default Select;
