import React from 'react';
import styled from 'styled-components';
import { Paragraph } from '@dotkomonline/design-system';
import Area from '../../Area';
import { Occation } from '../../../common/FormData';
import Select from '../../Inputs/Select';

interface CheckboxItem {
  value: Occation;
  label: string;
}

// Bedriftsarrangement, Annonse i Offline, Stillingsutlysning, Tech Talks og IT-ekskursjonen.
const occations: [CheckboxItem][] = [
  {
    label: 'Bedriftsarrangement',
    value: 'bedpres',
  },
  {
    label: 'Kurs',
    value: 'kurs',
  },
  {
    label: 'Annonse i Offline',
    value: 'offline',
  },
  {
    label: 'Stillingsutlysning',
    value: 'online_ad',
  },
  {
    label: 'Tech Talks',
    value: 'techtalks',
  },
  {
    label: 'IT-ekskursjonen',
    value: 'excursion',
  },
  {
    label: 'Annet',
    value: 'other',
  },
];

const S = {
  Div: styled.div`
    display: grid;
    grid-gap: 1rem;
    grid-template-rows: repeat(1fr);
    max-width: 500px;
    width: 100%;
    margin: 0 auto;
  `,
};
// eslint-disable-next-line @typescript-eslint/explicit-module-boundary-types
const OccationArea = () => (
  <Area title="Hva er dere interessert i?">
    <Paragraph>Kryss av for den anledningen fakturaen skal sendes for</Paragraph>
    <S.Div>
      <Select
        options={occations.map((occation) => ({
          ...occation,
          value: occation.value as string
        }))}
       />
    </S.Div>
  </Area>
);

export default OccationArea;
