import React from 'react';
import styled from 'styled-components';
import Area from '../../Area';
import Checkbox from '../../Inputs/Checkbox';
import { Paragraph } from '@dotkomonline/design-system';
import { Occation } from '../../../common/FormData';

interface CheckboxItem {
  label: string;
  value: Occation;
}

// Bedriftsarrangement, Annonse i Offline, Stillingsutlysning, Tech Talks og IT-ekskursjonen.
const interests: CheckboxItem[] = [
  {
    label: 'Bedriftsarrangement',
    value: 'bedpres',
  },
  {
    label: 'Kurs',
    value: 'kurs',
  },
  {
    label: 'Annonse i Offline',
    value: 'offline',
  },
  {
    label: 'Stillingsutlysning',
    value: 'online_ad',
  },
  {
    label: 'Tech Talks',
    value: 'techtalks',
  },
  {
    label: 'IT-ekskursjonen',
    value: 'excursion',
  },
  {
    label: 'Annet',
    value: 'other',
  },
];

const OccationArea = () => {
  return (
    <Area title="Hva er dere interessert i?">
      <Paragraph>
        Kryss av for den anledningen fakturaen skal sendes for
      </Paragraph>
      <S.Div>
        {interests.map((interest) => (
          <Checkbox name="interests" label={interest.label} value={interest.value} key={interest.value} />
        ))}
      </S.Div>
    </Area>
  );
};

const S = {
  Div: styled.div`
    display: grid;
    grid-gap: 1rem;
    grid-template-rows: repeat(1fr);
    max-width: 500px;
    width: 100%;
    margin: 0 auto;
  `,
};

export default OccationArea;
